package app.before;import DAO.Conexion;import javax.swing.*;import java.awt.*;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;/** * Clase previa encargada de mostrar la p√°gina de inicio. */public class Begin extends JPanel{    private static JFrame main = new JFrame();    private JButton start = new JButton();    private JButton help = new JButton();    private ImageIcon ic = new ImageIcon("res/imagenes/help.png");    private ImageIcon flecha = new ImageIcon("res/imagenes/fAmarilla.png");    private Icon flechaAmarilla = new ImageIcon(flecha.getImage().getScaledInstance(512/10, 512/10, Image.SCALE_DEFAULT));    private PokedexFondo pf = new PokedexFondo();    /**     * Inicializa el frame del principio.     */    public static void init(){        main.setTitle("Pokedex");        main.setVisible(true);        main.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);        main.setLocation(200,40);        main.setResizable(false);        main.setSize(1600/4, 2846/4);        long  now = System.currentTimeMillis();        main.add(new Begin());    }    /**     * Construye un panel con sus componentes.     */    public Begin(){        setLayout(null);        initComponents();    }    private  void initComponents() {        setLayout(null);        start.setBounds(10, (2846 / 4) / 2 + 4, 60, 60);        start.setVisible(true);        start.setOpaque(true);        start.setContentAreaFilled(false);        start.setBorderPainted(false);        start.setBorder(null);        start.addActionListener(new ActionListener() {            @Override            public void actionPerformed(ActionEvent e) {                begin();            }        });        add(start);        help.setBounds(22, 31, 60, 60);        help.setIcon(new ImageIcon(ic.getImage().getScaledInstance(help.getWidth(), help.getHeight(), Image.SCALE_SMOOTH)));        help.setVisible(true);        help.setOpaque(true);        help.setContentAreaFilled(false);        help.setBorderPainted(false);        help.setBorder(null);        help.addActionListener(new ActionListener() {            @Override            public void actionPerformed(ActionEvent e) {                JOptionPane.showMessageDialog(help, "Pulsa en la flecha amarilla para abrir la Pokedex", "Ayuda", 1, flechaAmarilla);            }        });        add(help);        add(pf);    }    private final void begin(){        main.dispose();        Start.start();    }}